//Icon hover effect
const shoppingCart = document.getElementById("shopping_cart");
const search = document.querySelector("#search");

//Adding event
shoppingCart.addEventListener("mouseover", function () {
  shoppingCart.setAttribute('class', "fa-solid fa-cart-shopping fa-bounce");
  shoppingCart.style.cssText = "--fa-animation-duration:2s; --fa-bounce-start-scale-x: 1; --fa-bounce-start-scale-y: 1; --fa-bounce-jump-scale-x: 1; --fa-bounce-jump-scale-y: 1; --fa-bounce-land-scale-x: 1; --fa-bounce-land-scale-y: 1; --fa-bounce-rebound: 0;"
}, false);

shoppingCart.addEventListener("mouseout", function () {
  shoppingCart.style.cssText = "";
  shoppingCart.setAttribute('class', "fa-solid fa-cart-shopping");
}, false);


search.addEventListener("mouseover", function () {
  search.setAttribute('class', "fa-solid fa-magnifying-glass fa-beat-fade");
  search.style.cssText = "--fa-animation-duration:3s;"
}, false);
search.addEventListener("mouseout", function () {
  search.style.cssText = "";
  search.setAttribute('class', "fa-solid fa-magnifying-glass");
}, false);

//slider
const imgs = document.querySelectorAll(".slides img");
const navslide = document.querySelectorAll('.nav-slide input');
const index = navslide.length;


for (let i = 0; i < index; i++) {
  console.log(i);

  document.querySelector(`.nav-slide #input${i}`).addEventListener('click', () => {
    imgs[0].style.marginLeft = `${0 - 100 * i}%`
  })
};


