//Icon hover effect
const sh=document.getElementById("shopping_cart");
const search =document.querySelector("#search");

sh.addEventListener("mouseover", function() {
    sh.setAttribute('class',"fa-solid fa-cart-shopping fa-bounce");
    sh.style.cssText="--fa-animation-duration:2s; --fa-bounce-start-scale-x: 1; --fa-bounce-start-scale-y: 1; --fa-bounce-jump-scale-x: 1; --fa-bounce-jump-scale-y: 1; --fa-bounce-land-scale-x: 1; --fa-bounce-land-scale-y: 1; --fa-bounce-rebound: 0;"
  }, false);

sh.addEventListener("mouseout", function() {
    sh.style.cssText="";
    sh.setAttribute('class',"fa-solid fa-cart-shopping");
  }, false);


search.addEventListener("mouseover", function() {
    search.setAttribute('class',"fa-solid fa-magnifying-glass fa-beat-fade");
    search.style.cssText="--fa-animation-duration:3s;"
  }, false);

search.addEventListener("mouseout", function() {
    search.style.cssText="";
    search.setAttribute('class',"fa-solid fa-magnifying-glass");
  }, false);
  

